```python
def lcs(str1, str2, m, n):
    lcs = [[0] * (n+1) for _ in range(m+1)]

    for i in range(m+1):
        for j in range(n+1):
            if i == 0 or j == 0:
                lcs[i][j] = 0
            elif str1[i-1] == str2[j-1]:
                lcs[i][j] = lcs[i-1][j-1] + 1
            else:
                lcs[i][j] = max(lcs[i][j-1], lcs[i-1][j])
    return lcs[m][n]


def longest_common_subsequence(str1, str2):
    m = len(str1)
    n = len(str2)
    lcs_length = lcs(str1, str2, m, n)
    lcs_str = [""] * lcs_length

